import { useState } from 'react';
import "./App.css";

function App() {
  /* 변수 - return 밖에 선언 */
  let post = "서면 우동 맛집 ";

  /* useState */
  let [글제목,b] = useState(['남자 코트 추천', '강남 우동맛집','파이썬독학']);
  let [따봉] = useState(0); // 굳이 함수까지 2개 작성하지 않아도 된다 

  /* 함수 */




  /* 1. HTML  레이아웃 짤 때는 이렇게 return () 안에 다 짜야한다 */
  /* 2. return 안에는 하나의 태그로 시작해서 하나의 태그로 끝나야 한다, 병렬로 태그 2개 이상 금지 */
  return ( 
    <div className='App'>

      <div className='black-nav'>
        <h4>블로그임</h4>
      </div>

      <div className="list">
        <h4>{글제목[0]} <span onClick={()=>{{따봉}+1}}>👍</span>{따봉}</h4>
        <p>5월 21일 발행</p>
      </div>
      <div className="list">
        <h4>{글제목[1]}</h4>
        <p>5월 21일 발행</p>
      </div>
      <div className="list">
        <h4>{글제목[2]}</h4>
        <p>5월 21일 발행</p>
      </div>

    </div>
  );
}

export default App;
