import { useState } from "react";
import "./App.css";
import React, { useRef } from "react"; // üü°input ÏûêÎèô focusÏö©


function App() {
  /* üü¢Î≥ÄÏàò - return Î∞ñÏóê ÏÑ†Ïñ∏ */
  let post = "DESIGNED BT ICHIBAN";

  /* üü¢useState */
  let [Í∏ÄÏ†úÎ™©, Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω] = useState(["isseymiyake","Saint Laurent","Dior",]);
  let [Îî∞Î¥â, Îî∞Î¥âÎ≥ÄÍ≤Ω] = useState([0,0,0]); // Íµ≥Ïù¥ Ìï®ÏàòÍπåÏßÄ 2Í∞ú ÏûëÏÑ±ÌïòÏßÄ ÏïäÏïÑÎèÑ ÎêúÎã§
  let [modal,setModal] = useState(false) // Î™®Îã¨Ï∞Ω ÏÉÅÌÉúÎ•º ÎÇòÌÉÄÎÇ¥Í∏∞ ÏúÑÌïú state - ( ) ÏïàÏóê Îì§Ïñ¥Í∞ÄÎäî ÌòïÏãùÏùÄ Î≠êÎì† ÏÉÅÍ¥Ä ÏóÜÎã§
  let [title,setTitle] = useState(0)  // UIÏÉÅÌÉúÎ•º ÎÇòÌÉÄÎÇ¥Í∏∞ ÏúÑÌïú state
  let [inputValue,setInputValue] = useState('')  // input Í∞íÏùÑ Ï†ÄÏû•ÌïòÍ∏∞ ÏúÑÌïú state
  let [when,setWhen] = useState([]);

  /* üü°inputÏóê focus */
  const inputRef = useRef(null); // refÎ•º ÏÉùÏÑ±
  const handleFocus = () => {
    inputRef.current.focus(); // input ÏöîÏÜåÏóê focusÎ•º ÏÑ§Ï†ï
  };


  /* Ìï®Ïàò */

  /* 1. HTML  Î†àÏù¥ÏïÑÏõÉ Ïß§ ÎïåÎäî Ïù¥Î†áÍ≤å return () ÏïàÏóê Îã§ ÏßúÏïºÌïúÎã§ */
  /* 2. return ÏïàÏóêÎäî ÌïòÎÇòÏùò ÌÉúÍ∑∏Î°ú ÏãúÏûëÌï¥ÏÑú ÌïòÎÇòÏùò ÌÉúÍ∑∏Î°ú ÎÅùÎÇòÏïº ÌïúÎã§, Î≥ëÎ†¨Î°ú ÌÉúÍ∑∏ 2Í∞ú Ïù¥ÏÉÅ Í∏àÏßÄ */
  return (
    <div className='App'>


      {/* Î∏îÎ°úÍ∑∏ Ï†úÎ™© */}
      <div className='my-nav'>
        <h1>Ichiban's Fashion <br/> BLOG</h1>
      </div>


      {/* üü¢Î≤ÑÌäº */}
      <button onClick={() => {
          let copyÍ∏ÄÏ†úÎ™© = [...Í∏ÄÏ†úÎ™©];
          copyÍ∏ÄÏ†úÎ™©[0] = "Yohji Yamamoto";
          Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω(copyÍ∏ÄÏ†úÎ™©);
      }}>Í∏ÄÏàòÏ†ï</button>

      <button style={{marginLeft:'20px'}} onClick={() => {
          let ccopyÍ∏ÄÏ†úÎ™©Sort = [...Í∏ÄÏ†úÎ™©];
          ccopyÍ∏ÄÏ†úÎ™©Sort.sort();
          Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω(ccopyÍ∏ÄÏ†úÎ™©Sort);
      }}>Ï†ïÎ†¨</button>

      {/* üü¢Í∏ÄÎ™©Î°ù 3Í∞ú , ÌååÏõåÏΩîÎî© */}
      {/* <div className='list'> 
        <h4>{Í∏ÄÏ†úÎ™©[0]} <span onClick={() => { Îî∞Î¥âÎ≥ÄÍ≤Ω(Îî∞Î¥â + 1); }} > ‚ô•Ô∏èüëç </span> {Îî∞Î¥â} </h4> 
        <p>5Ïõî 21Ïùº Î∞úÌñâ</p>
      </div>
      <div className='list'>
        <h4>{Í∏ÄÏ†úÎ™©[1]}</h4>
        <p>5Ïõî 21Ïùº Î∞úÌñâ</p>
      </div>
      <div className='list'>
        <h4 onClick={()=>{ setModal(!modal)}}>{Í∏ÄÏ†úÎ™©[2]}</h4>
        <p>5Ïõî 21Ïùº Î∞úÌñâ</p>
      </div> */}

      {/* üü¢map Î∞òÎ≥µÎ¨∏ÏúºÎ°ú Í∏ÄÎ™©Î°ù ÏÉùÏÑ± */}
      {
        Í∏ÄÏ†úÎ™©.map((e,i)=>{
          const now = new Date();
          const formattedDate = `${currentDate.getFullYear()}-${currentDate.getMonth() + 1}-${currentDate.getDate()}`; // ÎÇ†Ïßú Ìè¨Îß∑ÌåÖ


          return(
          <div className='list' key={i}>
            {/* <h4 onClick={()=>{ setModal(!modal)}}>{Í∏ÄÏ†úÎ™©[2]}</h4> */}
            {/* <h4 onClick={()=>{ setModal(!modal)}}>{e}</h4> */}
            <h4 onClick={()=>{ setModal(!modal); setTitle(i)}}>{Í∏ÄÏ†úÎ™©[i]} 
             <span onClick={(e) => { 
              let copy = [...Îî∞Î¥â];
              copy[i] += 1;
              Îî∞Î¥âÎ≥ÄÍ≤Ω(copy);
               e.stopPropagation();}} 
             > ‚ù§Ô∏è </span> {Îî∞Î¥â[i]} 
            </h4>
            <p> {formattedDate}</p>
            <button onClick={()=>{
              let copyÍ∏ÄÏ†úÎ™© = [...Í∏ÄÏ†úÎ™©];
              copyÍ∏ÄÏ†úÎ™©.splice(i,1);
              Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω(copyÍ∏ÄÏ†úÎ™©);
            }}>ÏÇ≠Ï†ú</button>
          </div>

          )
        })
      }

      {/* üü¢Î™®Îã¨Ï∞Ω */}
      {
        modal==true ? <Modal title={title} Í∏ÄÏ†úÎ™©={Í∏ÄÏ†úÎ™©} color='skyblue' Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω={Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω} /> : null  
        // Î∂ÄÎ™® state Î∞õÏïÑÏò§Í∏∞ ‚≠ê
        // ÏùºÎ∞ò Î¨∏ÏûêÎèÑ Í∞ÄÎä•  ' ' ÏÇ¨Ïö©
        // Ìï®ÏàòÎèÑ Î∞õÏïÑ Ïò¨ Ïàò ÏûàÎã§ (state Ìï®Ïàò)
      }                                                                                     

      {/*üü¢Í∏Ä Î∞úÌñâ input + button */}
      <input
        ref={inputRef} // üü°ref ÏÜçÏÑ± Ï∂îÍ∞Ä
        onChange={(e) => {
          setInputValue(e.target.value);
        }}
        value={inputValue} // inputValue ÏÉÅÌÉúÎ•º input ÏöîÏÜåÏùò Í∞íÏúºÎ°ú ÏÑ§Ï†ï
      />


      <button className='btn-create' onClick={(e)=>{
        let copyÍ∏ÄÏ†úÎ™© = [...Í∏ÄÏ†úÎ™©];
        copyÍ∏ÄÏ†úÎ™©.unshift(inputValue);

        //Í∏Ä Ï∂îÍ∞Ä Îê† Îïå ÎßàÎã§ Îî∞Î¥âÍ∞úÏàò Ï†ÄÏû• ÏàòÎèÑ Ï¶ùÍ∞Ä
        let copyÎî∞Î¥â = [...Îî∞Î¥â]
        copyÎî∞Î¥â.unshift(0);
        Îî∞Î¥âÎ≥ÄÍ≤Ω(copyÎî∞Î¥â);

        //inputÏóê 1Í∞ú Ïù¥ÏÉÅÏùò Í∏ÄÏûêÍ∞Ä ÏûÖÎ†•ÎêòÏñ¥Ïïº Í∏Ä Ï∂îÍ∞Ä Í∞ÄÎä•
        !!inputValue ? Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω(copyÍ∏ÄÏ†úÎ™©) : false;
        setInputValue('');

        handleFocus(); // üü°Î≤ÑÌäº ÌÅ¥Î¶≠ ÌõÑ inputÏóê focusÎ•º ÏÑ§Ï†ï
      }}>Í∏Ä Î∞úÌñâ</button>


    </div> /* Î©îÏù∏ div ÁµÇ */
  );
}

/* üü¢Î™®Îã¨ */
function Modal(props) { // propsÎ°ú Î∂ÄÎ™® state Î∞õÏïÑÏò§Í∏∞ ‚≠ê
  return(
    <div className='modal' style={{background : props.color}}>
      <h4>{props.Í∏ÄÏ†úÎ™©[props.title]}</h4>
      <p>ÎÇ†Ïßú</p>
      <p>ÏÉÅÏÑ∏ÎÇ¥Ïö©</p>
      <button 
      //   onClick={()=>{
      //     let copy = [...props.Í∏ÄÏ†úÎ™©]
      //     copy[0] = 'Ïó¨ÏûêÏΩîÌä∏ Ï∂îÏ≤ú';
      //     props.Í∏ÄÏ†úÎ™©Î≥ÄÍ≤Ω(copy)
      //   }}
      >Í∏ÄÏàòÏ†ï</button>
    </div>
  )
}


export default App;
